<?phpnamespace teamsystem\data\team\avatar;use wcf\data\user\avatar\IUserAvatar;use wcf\system\WCF;use wcf\util\StringUtil;/** * Class DefaultTeamAvatar * @package teamsystem\data\team\avatar */class DefaultTeamAvatar implements IUserAvatar  {    /**     * image size     * @var	integer     */    public $size = TeamAvatar::AVATAR_SIZE;    /**     * @inheritDoc     */    public function getURL($size = null) {        return WCF::getPath('teamsystem').'images/avatars/avatar-default.png';    }    /**     * @inheritDoc     */    public function getImageTag($size = null) {        if ($size === null) $size = $this->size;        return '<img src="'.StringUtil::encodeHTML($this->getURL($size)).'" style="width: '.$size.'px; height: '.$size.'px" alt="" class="userAvatarImage">';    }    /**     * @inheritDoc     */    public function getWidth() {        return $this->size;    }    /**     * @inheritDoc     */    public function getHeight() {        return $this->size;    }    /**     * @inheritDoc     */    public function canCrop() {        return false;    }    /**     * @inheritDoc     */    public function getCropImageTag($size = null) {        return '';    }}